import axios from 'axios';
import React, { Component, Fragment } from 'react'
import {Container,Row,Col,Card} from 'react-bootstrap'
import AppURL from '../../api/AppURL';

class Categories extends Component {

     constructor() {
          super();
          this.state = {
               MenuData: []
          }
     }

     componentDidMount() {
          axios.get(AppURL.AllCategoryDetails).then(response => {
               this.setState({ MenuData: response.data });

          }).catch(error => {

          });
     }
     render() {

          const CatList =this.state.MenuData;
          const MyView=CatList.map((CatList,i)=>{
               return  <Col key={i.toString()} className="p-0" key={1} xl={3} lg={3} md={3} sm={6} xs={6}>
               <Card className="h-100 w-100 text-center">          
               <Card.Body> 
               <img className="center" src="https://rukminim1.flixcart.com/flap/128/128/image/f15c02bfeb02d15d.png?q=100" />  
               <h5 className="category-name">Top Offers</h5>  
               </Card.Body>
               </Card> 
               </Col>
          })
          return (
               <Fragment>
                  <Container className="text-center" fluid={true}>
          <div className="section-title text-center mb-55"><h2> CATEGORIES</h2>
          <p>Some Of Our Exclusive Collection, You May Like</p>
          </div>

          <Row>
          </Row>

                    </Container>
               </Fragment>
          )
     }
}

export default Categories
