import React, { Component, Fragment } from "react";
import { Container, Row, Col, Form, Button } from "react-bootstrap";

export class Contact extends Component {
     constructor() {
          super();
          this.state = {
               name: "",
               email: "",
               message: "",
          };
     }

     nameOnChange = (event) => {
          let name = event.target.value;
          // alert(name);
          this.setState({ name: name });
     };

     emailOnChange = (event) => {
          let email = event.target.value;
          // alert(email);
          this.setState({ email: email });
     };

     messageOnChange = (event) => {
          let message = event.target.value;
          // alert(message);
          this.setState({ message: message });
     };

     onFormSubmit = (event) => {
          alert("Hello hi");
          event.prventDefault();
     };

     render() {
          return (
               <Fragment>
                    <Container>
                         <Row className="p-2">
                              <Col
                                   className="shadow-sm bg-white mt-2"
                                   md={12}
                                   lg={12}
                                   sm={12}
                                   xs={12}
                              >
                                   <Row className="text-center">
                                        <Col
                                             className="d-flex justify-content-center"
                                             md={6}
                                             lg={6}
                                             sm={12}
                                             xs={12}
                                        >
                                             <Form onSubmit={this.onFormSubmit} className="onboardForm">
                                                  <h4 className="section-title-login">CONTACT WITH US </h4>
                                                  <h6 className="section-sub-title">
                                                       Please Contact With Us{" "}
                                                  </h6>
                                                  <input
                                                       onChange={this.nameOnChange}
                                                       className="form-control m-2"
                                                       type="text"
                                                       placeholder="Enter Your Name"
                                                  />

                                                  <input
                                                       onChange={this.emailOnChange}
                                                       className="form-control m-2"
                                                       type="email"
                                                       placeholder="Enter Email"
                                                  />

                                                  <Form.Control
                                                       onChange={this.messageOnChange}
                                                       className="form-control m-2"
                                                       as="textarea"
                                                       rows={3}
                                                       placeholder="Message"
                                                  />

                                                  <Button
                                                       type="submit"
                                                       className="btn btn-block m-2 site-btn-login"
                                                  >
                                                       {" "}
                                                       Send{" "}
                                                  </Button>
                                             </Form>
                                        </Col>

                                        <Col className="p-0 Desktop m-0" md={6} lg={6} sm={6} xs={6}>
                                             <br></br>
                                             <br></br>
                                             <p className="section-title-contact">
                                                  302, N.D. Tower,Raozan,Chittagong <br></br>
                                                  Email: support@electromart.com
                                             </p>

                                             <iframe
                                                  src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d1302.916027848214!2d91.91998505994528!3d22.534068716543427!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x0%3A0x3ad66eac0e3183d5!2sRECONS!5e0!3m2!1sen!2sbd!4v1636556827578!5m2!1sen!2sbd"
                                                  width="550"
                                                  height="450"
                                                  styles="border:0;"
                                                  allowfullscreen=""
                                                  loading="lazy"
                                             ></iframe>
                                        </Col>
                                   </Row>
                              </Col>
                         </Row>
                    </Container>
               </Fragment>
          );
     }
}

export default Contact;
